Scripts.OnMouseWheel:
if (delta==-1) then
	ShamanPower:SealCycle();
else
	ShamanPower:SealCycleBackward();
end

if InCombatLockdown() then return false end
if (delta==-1) then  --mouse wheel down
	ShamanPower:PerformAuraCycle(ShamanPower.player, true);
else
	ShamanPower:PerformAuraCycleBackwards(ShamanPower.player, true);
end
ShamanPower:ButtonsUpdate();

if InCombatLockdown() then return false end
if (delta==-1) then --mouse wheel down
	ShamanPower:PerformCycle(ShamanPower.player, self:GetAttribute("classID"), true);
else
	ShamanPower:PerformCycleBackwards(ShamanPower.player, self:GetAttribute("classID"), true);
end
ShamanPower:ButtonsUpdate();

if InCombatLockdown() then return false end
local pname = (ShamanPower:GetUnit(self:GetAttribute("classID"), self:GetAttribute("playerID"))).name
ShamanPower:PerformPlayerCycle(delta, pname, self:GetAttribute("classID"));
ShamanPower:ButtonsUpdate();

Scripts.OnEnter:
if ShamanPower.opt.ShowTooltips then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	GameTooltip:SetText(ShamanPower.CONFIG_DRAGHANDLE);
	GameTooltip:Show(self, motion);
	CursorUpdate(self, motion);
end

if ShamanPower.opt.ShowTooltips then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	GameTooltip:SetText(ShamanPower.CONFIG_RESIZEGRIP);
	GameTooltip:Show(self, motion);
	CursorUpdate(self, motion);
end

if ShamanPower.opt.ShowTooltips then
	GameTooltip:SetOwner(self, "ANCHOR_BOTTOM");
	GameTooltip:SetText(SHAMANPOWER_REFRESH_DESC);
	GameTooltip:Show(self, motion);
	CursorUpdate(self, motion);
end

if ShamanPower.opt.ShowTooltips then
	GameTooltip:SetOwner(self, "ANCHOR_BOTTOM");
	GameTooltip:SetText(SHAMANPOWER_CLEAR_DESC);
	GameTooltip:Show(self, motion);
	CursorUpdate(self, motion);
end

if ShamanPower.opt.ShowTooltips then
	GameTooltip:SetOwner(self, "ANCHOR_BOTTOM");
	GameTooltip:SetText(SHAMANPOWER_AUTOASSIGN_DESC);
	GameTooltip:Show(self, motion);
	CursorUpdate(self, motion);
end

if ShamanPower.opt.ShowTooltips then
	GameTooltip:SetOwner(self, "ANCHOR_BOTTOM");
	GameTooltip:SetText(SHAMANPOWER_OPTIONS_DESC);
	GameTooltip:Show(self, motion);
	CursorUpdate(self, motion);
end

if ShamanPower.opt.ShowTooltips then
	GameTooltip:SetOwner(self, "ANCHOR_BOTTOM");
	GameTooltip:SetText("Open Totem Range tracker configuration");
	GameTooltip:Show(self, motion);
	CursorUpdate(self, motion);
end

if ShamanPower.opt.ShowTooltips then
	GameTooltip:SetOwner(self, "ANCHOR_BOTTOM");
	GameTooltip:SetText(SHAMANPOWER_PRESET_DESC);
	GameTooltip:Show(self, motion);
	CursorUpdate(self, motion);
end

Scripts.OnClick:
if ShamanPower.opt.ReportChannel >0 then
	ShamanPower:Report("CHANNEL", ShamanPower.opt.ReportChannel);
else
	ShamanPower:Report();
end

